import * as d3 from "d3";
import React, { useEffect, useRef} from "react"; 
import mapdata from "./data.csv";
import recieveCountry from "./VisitedCountries";
import Wahoo from "./Test.jsx";
const WorldMap = () => {
  const ref = useRef();
  const [countryRender, setCountry] = ("");
  useEffect(() => {
    // set the dimensions and margins of the graph

    const width = 900;
    const height = 900;

    const svg = d3
      .select(ref.current)
      .append("svg")
      .attr("width", width)
      .attr("height",height)

    // Map and projection
    const path = d3.geoPath();
    const projection = d3.geoMercator()

    .center([0,20])
    .translate([width / 2, height / 2]);
  
  // Data and color scale
  const data = new Map();
  const colorScale = d3.scaleLinear()
  .domain([0,1])
  .range(["grey","blue"]);
  
  // Load external data and boot
  Promise.all([
  d3.json("https://raw.githubusercontent.com/holtzy/D3-graph-gallery/master/DATA/world.geojson"),
  d3.csv(mapdata, function(d) {
      data.set(d.code, +d.visited)
  })]).then(function(loadData){
      let topo = loadData[0]
  
      let mouseOver = function(d) {
      d3.selectAll(".Country")
        .transition()
        .duration(200)
        .style("opacity", .5)
      d3.select(this)
        .transition()
        .duration(200)
        .style("opacity", 1)
        .style("stroke", "black")
    }
  
    let mouseLeave = function(d) {
      d3.selectAll(".Country")
        .transition()
        .duration(200)
        .style("opacity", .8)
      d3.select(this)
        .transition()
        .duration(200)
        .style("stroke", "transparent")
    }

    let mouseClick = function (event, d) {
            d3.selectAll(".Country")
              .transition()
              .duration(200)
              .style("opacity", 0.8);
            d3.select(this)
              .transition()
              .duration(200)
              .style("stroke", "transparent");
              setCountry(d.properties.name); // Log the country name
              console.log(countryRender);
          };
    
    // Draw the map
    svg.append("g")
      .selectAll("path")
      .data(topo.features)
      .enter()
      .append("path")
        // draw each country
        .attr("d", d3.geoPath()
          .projection(projection)
        )
        // set the color of each country
        .attr("fill", function (d) {
          d.total = data.get(d.id) || 0;
          return colorScale(d.total);
        })
        .style("stroke", "transparent")
        .attr("class", function(d){ return "Country" } )
        .style("opacity", .8)
        .on("mouseover", mouseOver )
        .on("mouseleave", mouseLeave )
        .on("click", mouseClick); // Attach the mouseClick function
  
  });
  }, []);

  return <div>
    <svg width={900} height={900} id="WorldMap" ref={ref} />
    <recieveCountry name = {countryRender}/>
    </div>;
};

export default WorldMap;
