import React, { useEffect, useRef,useState} from "react"; 
import * as d3 from "d3";
import mapdata from "./data.csv";

const country_info = {
  "USA" : ["./CountryImages/USA.jpg","Smallest directly families surprise honoured am an. Speaking replying mistress him numerous she returned feelings may day. Evening way luckily son exposed get general greatly. Zealously prevailed be arranging do. Set arranging too dejection september happiness. Understood instrument or do connection no appearance do invitation. Dried quick round it or order. Add past see west felt did any. Say out noise you taste merry plate you share. My resolve arrived is we chamber be removal. On on produce colonel pointed. Just four sold need over how any. In to september suspicion determine he prevailed admitting. On adapted an as affixed limited on. Giving cousin warmly things no spring mr be abroad. Relation breeding be as repeated strictly followed margaret. One gravity son brought shyness waiting regular led ham."],
  "Canada" : ["./CountryImages/Canada.jpg","Smallest directly families"],
  "Ireland" : ["./CountryImages/Ireland.jpg","Smallest directly families"],
  "Egypt" : ["./CountryImages/Egypt.jpg","Smallest directly families"],
  "Czech Republic" : ["./CountryImages/CzechRepublic.jpg","Smallest directly families"],
  "Germany" : ["./CountryImages/Germany.jpg","Smallest directly families"],
  "France" : ["./CountryImages/France.jpg","Smallest directly families"],
  "Greece" : ["./CountryImages/Greece.jpg","Smallest directly families"],
  "Iceland" : ["./CountryImages/Iceland.jpg","A friend and I took a road trip around the ring of Iceland and stopped along the way to see many of the natural landmarks and attractions. The landscape was alien, barren, and constantly changing. It is about 1/100 the size of Canada and has a population around the same ratio. The cost of living is quite high. We made the mistake of assuming the cereal and coffee was complementary, when it was 19 dollars for corn flakes. However, some things were exceptionally affordable, like the dairy industry. We bought a large block of quality cheese which lasted us the entire trip.","This is a picture of Akureyri, the second largest city in Iceland and marked the halfway point of the trip. The lights of the city reflecting off the water had a magical quality to it which reminded me of my favourite Christmas movie, The Polar Express, when the train pulls into Santaâ€™s village. "],
  "Italy" : ["./CountryImages/Italy.jpg","Smallest directly families"],
  "Japan" : ["./CountryImages/Japan.jpg","Smallest directly families"],
  "Mexico" : ["./CountryImages/Mexico.jpg","Smallest directly families"],
  "Norway" : ["./CountryImages/Norway.jpg","Smallest directly families"],
  "Poland" : ["./CountryImages/Poland.jpg","Smallest directly families"],
  "Thailand" : ["./CountryImages/Thailand.jpg","Smallest directly families"]
};

function ForLoopParagraphs(props){
  let array = Object.values(props);
  console.log(array);
  for(let i=0; i < array.length; i++ ){
    return(
    <div>             
    <p className = "Text separateparagraphs">
      {array[i]}
    </p>
    </div> );
  }
}
function RecieveCountry(props) {
    let visitedCountries = ["USA","Canada","Ireland","Egypt","Czech Republic","Germany","France","Greece","Iceland","Italy","Japan","Mexico","Norway","Poland","Thailand"];
    let contains = visitedCountries.includes(props.name);
    if(contains){
      let info = country_info[props.name];
        return(
            <div style={{textAlign: "center"}} >
              <img src = {info[0]} className = "photo"/>
              <ForLoopParagraphs props = {info.slice(1,info.length)}/>
            </div>
        );
    }else{
        return(
            <div></div>
        );
    }

}

const WorldMap = () => {
  const ref = useRef();
  const ref2 = useRef();
  const [Country, setCountry] = useState("");
  useEffect(() => {
    // set the dimensions and margins of the graph
    const width = 900;
    const height = 900;
    
    const svg = d3
      .select(ref.current)
      .append("svg")
      .attr("width", width)
      .attr("height",height)

    // Map and projection
    const path = d3.geoPath();
    const projection = d3.geoMercator()

    .center([0,20])
    .translate([width / 2, height / 2]);
  
  // Data and color scale
  const data = new Map();
  const colorScale = d3.scaleLinear()
  .domain([0,1])
  .range(["grey","blue"]);
  
  // Load external data and boot
  Promise.all([
  d3.json("https://raw.githubusercontent.com/holtzy/D3-graph-gallery/master/DATA/world.geojson"),
  d3.csv(mapdata, function(d) {
      data.set(d.code, +d.visited)
  })]).then(function(loadData){
      let topo = loadData[0]
  
      let mouseOver = function(d) {
      d3.selectAll(".Country")
        .transition()
        .duration(200)
        .style("opacity", .5)
      d3.select(this)
        .transition()
        .duration(200)
        .style("opacity", 1)
        .style("stroke", "black")
    }
  
    let mouseLeave = function(d) {
      d3.selectAll(".Country")
        .transition()
        .duration(200)
        .style("opacity", .8)
      d3.select(this)
        .transition()
        .duration(200)
        .style("stroke", "transparent")
    }

    let mouseClick = function (event, d) {
            d3.selectAll(".Country")
              .transition()
              .duration(200)
              .style("opacity", 0.8);
            d3.select(this)
              .transition()
              .duration(200)
              .style("stroke", "transparent");
              setCountry(d.properties.name);
          };
    
    // Draw the map
    svg.append("g")
      .selectAll("path")
      .data(topo.features)
      .enter()
      .append("path")
        // draw each country
        .attr("d", d3.geoPath()
          .projection(projection)
        )
        // set the color of each country
        .attr("fill", function (d) {
          d.total = data.get(d.id) || 0;
          return colorScale(d.total);
        })
        .style("stroke", "transparent")
        .attr("class", function(d){ return "Country" } )
        .style("opacity", .8)
        .on("mouseover", mouseOver )
        .on("mouseleave", mouseLeave )
        .on("click", mouseClick); // Attach the mouseClick function
  
  });
  }, []);
  
  return (<div className="worldTravel">
  <div class="map">
    <svg viewBox="0 0 900 900" id="WorldMap" ref={ref} />
    </div>
  <div>
    <RecieveCountry name={Country} />
  </div>
</div>);
};

export default WorldMap;
